using GSMA.MobileConnect.Authentication;
using Newtonsoft.Json;
using NUnit.Framework;
using System;

namespace GSMA.MobileConnect.Test.Authentication
{
    [TestFixture, Parallelizable]
    public class RequestTokenResponseDataTests
    {
        [Test]
        public void ResponseDataShouldDeserialize()
        {
            var response = "{\"time_received\":\"2016-01-01T01:01:00\",\"access_token\":\"-pM0e0SjymNH9QMekbamDFDYiydNGO4X3YeK1Df_3Zo\",\"token_type\":\"Bearer\",\"expires_in\":3600,\"refresh_token\":null,\"id_token\":\"eyJhbGciOiJSUzI1NiIsImtpZCI6IlBIUE9QLTAwIn0..lF2e4yh42-n4MxoOXGd6RzFjfmqrw_d_ilP9UGkabctJMx54SRH3Xyl44cy-UNGCghX7aEq0IcBTDjAo_fF9SKOfLnnEVHP-rJ1Z4hTbbbhdu-Tp_gYAo1YAkkfRg-p9np_amM058-irnbdfb5T10rhJjtp7bs6AklNJ6XhuFgk\"}";

            var actual = JsonConvert.DeserializeObject<RequestTokenResponseData>(response);

            Assert.AreEqual("-pM0e0SjymNH9QMekbamDFDYiydNGO4X3YeK1Df_3Zo", actual.AccessToken);
            Assert.AreEqual("Bearer", actual.TokenType);
            Assert.AreEqual(3600, actual.ExpiresIn);
            Assert.AreEqual("eyJhbGciOiJSUzI1NiIsImtpZCI6IlBIUE9QLTAwIn0..lF2e4yh42-n4MxoOXGd6RzFjfmqrw_d_ilP9UGkabctJMx54SRH3Xyl44cy-UNGCghX7aEq0IcBTDjAo_fF9SKOfLnnEVHP-rJ1Z4hTbbbhdu-Tp_gYAo1YAkkfRg-p9np_amM058-irnbdfb5T10rhJjtp7bs6AklNJ6XhuFgk",
                actual.IdToken);
            Assert.AreEqual(new DateTime(2016, 1, 1, 1, 1, 0), actual.TimeReceived);
            Assert.AreEqual(new DateTime(2016, 1, 1, 2, 1, 0), actual.Expiry);
            Assert.IsNull(actual.RefreshToken);
        }
    }
}